{
	abEntry: {
		criteria: {
			searchQuery: {
				"$AND": [
                    {
						"type": {
							"$EQ": "Company"
						}
					},
					{
						"$OR": [
                            {
								companyName: {
									"$LIKE": "del%"
								}
							},
							{
								companyName: {
									"$LIKE": "dup%"
								}
							}
						]
					}
				]
			},
			top: 3
		},
		scope: {
			fields: {
				key: 1,
				type: 1,
				companyName: 1
			}
		},
	},
	configuration: {
		drivers: {
			"IAbEntrySearcher": "Maximizer.Model.Access.Sql.AbEntrySearcher"
		}
	}
}

{
	abEntry: {
		criteria: {
			searchQuery: {
                "$PHRASE":"access -steven address:maple"
			},
			top: 33
		},
		scope: {
			fields: {
				displayValue: 1,
                address: {
                    displayValue: 1
                }
			}
		},
	},
	configuration: {
		drivers: {
			"IAbEntrySearcher": "Maximizer.Model.Access.Sql.AbEntrySearcher"
		}
	}
}

{
	"abEntry.HasOpp": {
		criteria: {
			searchQuery: {
                key: {
                    "$EXISTS(OpportunityObject)": "abEntryKey"
                }
			},
			top: 3
		},
		scope: {
			fields: {
				displayValue: 1,
			}
		},
	},
 	"abEntry.HasAbandonedOpp": {
		criteria: {
			searchQuery: {
                key: {
                    "$EXISTS(OpportunityObject)":{
                        field: "abEntryKey",
                        query:{
                            status:{
                                "$EQ":5
                            }
                        }
                    }
                }
			},
			top: 3
		},
		scope: {
			fields: {
				displayValue: 1,
			}
		},
	},
    opportunity:{
        criteria:{
            searchQuery:{
                status: {
                    "$EQ":5
                }
            }
        },
        scope:{
            fields:{
                objective: 1,
                status: {
                    value: 1,
                    displayValue: 1,
                },
                abEntry:{
                    displayValue: 1
                }
            }
        }
    },	
    configuration: {
		drivers: {
			"IAbEntrySearcher": "Maximizer.Model.Access.Sql.AbEntrySearcher",
			"IOpportunitySearcher": "Maximizer.Model.Access.Sql.OpportunitySearcher"
		}
	}
}

